customCSS:
.error-box{
    color:red;
    padding:50px;
    border:1px solid red;
    border-radius:4px;
}

.footer-info{
    background-color: rgb(93, 134, 46);
    padding: 15px 0 0 0 !important;
    height:80px;
    width:100%;
}

.footer-content-container{
	width:100%;
    background-color: #7F8084;
}
.footer-content{
    background-color: #7F8084;
    color:#FFFFFF;
    height:65px;
    padding: 0 10px 0 10px;
    width:100%;
    max-width:1440px;
    margin:auto;
}

.footer-social{
	padding:18px 12px 12px 0px; 
    float:right;
}
.footer-social a{
	margin-left:6px;
}
.footer-social a img{
	width:24px;
    height:24px;
}

.register-container{
    padding:50px;
    border:1px solid #333;
    border-radius:4px;
}
.register-content{
    text-align:center;
}
.register-body,
.register-header{
    padding:12px ;
}
.register-button{
    display:block;
    padding:12px 24px;
    border-radius:4px;
    background-color:rgb(93, 134, 46);
    color:white !important;
    width:fit-content;
    margin:12px auto;
}
.register-app-header{
    font-size:16px;
    color:93, 134, 46;
    text-align:center;
    padding:12px 12px 12px 12px;

}
.register-app-body{
    width:fit-content;
    text-align:center;
   margin:0 auto;
}
.register-app-container {
    min-height:240px;
    padding:50px;
    border:1px solid #333;
    border-radius:4px;
}
.register-app-button{
    display:block;
    padding:12px 24px 12px 24px;
    border-radius:4px;
    background-color:rgb(93, 134, 46);
    color:white !important;
    width:fit-content;
    margin:12px auto 12px auto;
}
.forum-container{
    padding:40px;
    border:0px solid #333;
    border-radius:4px;
    background-color:rgb(127, 128, 132);
}
.forum-content{
    text-align:center;
    color:white;
}
.forum-body,
.forum-header{
    padding:12px
}
.forum-button{
    display:block;
    padding:12px 24px;
    border-radius:4px;
    background-color:rgb(93, 134, 46);
    color:white !important;
    width:fit-content;
    margin:12px auto;
}

.static-container{
    background-image:url(../../../sfsites/c/file-asset/manpointing?v=1);
    height:250px;
    background-position:center;
    background-repeat:no-repeat;
    display:grid;
    grid-template-columns:50% 50%;
    border-radius:4px;
}
.static-content{
    text-align:center;
    grid-column:2;
    vertical-align: middle;
    margin: auto;
}
.static-body,
.static-header{
    padding:12px
}
.static-button{
    display:block;
    padding:12px 24px;
    border-radius:4px;
    background-color:rgb(93, 134, 46);
    color:white !important;
    width:fit-content;
    margin:12px auto;
}

.breadcrumb-container{
    margin-top: 4pt;
    color: #333 !important;
    font-size: 12px;
}

.breadcrumb,
.breadcrumb-last{
    margin-right: 16pt;
    color: #333 !important;
}
.breadcrumb-last{
    font-weight:bold;
}
.breadcrumb-hr{
    border:1px solid rgb(93, 134, 46);
    margin: 16pt 0pt;
}

.forceChatterScroller{
    width:100% !important;
}
